mapper.update:
update beauty_table_config
table_name = #{tableName,jdbcType=VARCHAR}, title = #{title,jdbcType=VARCHAR}, data = #{data,jdbcType=VARCHAR}, column_name = #{columnName,jdbcType=VARCHAR}, orderable = #{orderable,jdbcType=TINYINT}, width = #{width,jdbcType=INTEGER}, class_name = #{className,jdbcType=VARCHAR}, sequence = #{sequence,jdbcType=INTEGER}, render = #{render,jdbcType=VARCHAR}, remark = #{remark,jdbcType=VARCHAR}, creater = #{creater,jdbcType=VARCHAR}, modifier = #{modifier,jdbcType=VARCHAR}, is_deleted = #{isDeleted,jdbcType=TINYINT}, def1 = #{def1,jdbcType=VARCHAR}, def2 = #{def2,jdbcType=VARCHAR}, def3 = #{def3,jdbcType=TIMESTAMP}, last_update_time = NOW()
