mapper.select:
SELECT DISTINCT t2.id, t2.column_name, t2.title FROM information_schema.`COLUMNS` t1 LEFT JOIN beauty_conf_columns t2 ON t1.COLUMN_NAME = t2.column_name WHERE t1.TABLE_SCHEMA = (SELECT dict_value FROM beauty_dict WHERE dict_key = 'schema') AND t1.TABLE_NAME = #{tableName} AND NOT EXISTS (SELECT 1 FROM beauty_table_columns_relation WHERE column_id = t2.id)
SELECT t1.id, t2.column_name, t2.title FROM beauty_table_columns_relation t1 LEFT JOIN beauty_conf_columns t2 ON t1.column_id = t2.id WHERE t1.grid_name = #{tableName}
ORDER BY
${orderColumn} ${orderDir}
id
LIMIT #{from}, #{size}
