mapper.select:
SELECT id, `code`, `name` FROM beauty_authority a WHERE id NOT IN (SELECT authority_id FROM beauty_role_authority t WHERE t.role_id = #{roleId})
ORDER BY
${orderColumn} ${orderDir}
id
LIMIT #{from}, #{size}

SELECT ra.id, ra.role_id, a.id AS authority_id, a.`code`, a.`name`
FROM beauty_role_authority ra
LEFT JOIN beauty_authority a ON a.id = ra.authority_id
WHERE ra.role_id = #{roleId}

ORDER BY

	${orderColumn} ${orderDir}


	id

LIMIT #{from}, #{size}

mapper.delete:
delete from beauty_role_authority where 1 != 1
OR id IN
#{id}

mapper.update:
update beauty_role_authority
role_id = #{roleId,jdbcType=BIGINT}, authority_id = #{authorityId,jdbcType=BIGINT},
where id = #{id,jdbcType=BIGINT}
